var Class = System.getModule("com.vmware.pscoe.library.class").Class();
var InputSpecBase = System.getModule("com.vmware.pscoe.library.install.spec").InputSpecBase();


return Class.define(function BooleanInputSpec(path) {
	InputSpecBase.apply(this, arguments);
	this.validators = [function (value) {
		return value && typeof (value) !== "boolean" ? "boolean value expected, but got " + typeof (value) : null;
	}];
}, {}, InputSpecBase);
