dunes-script-module.script:
var Class = System.getModule("com.vmware.pscoe.library.class").Class();

var ResolverFactory = Class.define(function ResolverFactory() {
    throw new Error("Private constructor");
}, null);

ResolverFactory.Type = {
    PerExecution: "PerExecution",
    Singleton: "Singleton"
}

var mapper = {
    PerExecution: "PerExecutionResolver",
    Singleton: "SingletonResolver"
}

ResolverFactory.getResolver = function (type, properties) {
    if (!mapper[type]) {
        throw new Error("Resolver of type: " + type + "not found");
    }
    var resolver = Class.load("com.vmware.pscoe.library.logging.appenders.resource.element.resolvers", mapper[type]);
    return new resolver(properties);
}

return ResolverFactory;
