dunes-script-module.script:
var exports = {};
var ExtractorUtil = /** @class */ (function () {
    function ExtractorUtil() {
    }
    ExtractorUtil.getExtractedVmPrefix = function (extractedVmName) {
        var indexOfFirstDigit = extractedVmName.search(/\d/i);
        var extractedVmPrefix = "";
        if (indexOfFirstDigit > -1) {
            extractedVmPrefix = extractedVmName.slice(0, indexOfFirstDigit);
        }
        return extractedVmPrefix;
    };
    ExtractorUtil.getExtractedVmSuffix = function (extractedVmName, templatePostfix) {
        var extractedVmSuffix = "";
        if (templatePostfix.length === 0) {
            var vmLastChar = extractedVmName.charAt(extractedVmName.length - 1);
            if (isNaN(vmLastChar)) {
                extractedVmSuffix = vmLastChar;
            }
        }
        else {
            extractedVmSuffix = extractedVmName.slice(extractedVmName.length - templatePostfix.length);
        }
        return extractedVmSuffix;
    };
    return ExtractorUtil;
}());
exports.ExtractorUtil = ExtractorUtil;
return exports;
