var XmlUtil = /** @class */ (function () {
    function XmlUtil() {
        throw new Error("Private constructor");
    }
    XmlUtil.json2xml = function (obj) {
        if (obj === Object(obj)) {
            return Object.keys(obj).map(function (k) { return "<" + k + ">" + XmlUtil.json2xml(obj[k]) + "</" + k + ">"; }).join("");
        }
        else {
            return obj;
        }
    };
    return XmlUtil;
}());
exports.XmlUtil = XmlUtil;
return exports;
