dunes-script-module.script:
if (!datacenter || !environment) {
    throw new Error("Missing required input parameter. Got datacenter=" + datacenter + " enviroment=" + environment);
}
var albEndPoint = System.getModule("com.vmware.pscoe.nyc-doe.ts-vrac-ng-automation.actions.actions.avi").getAlbEndPoint(datacenter, environment);
var result = new Properties();
if (albEndPoint !== null) {
    result.put('avi_endpoint', albEndPoint.avi_endpoint);
    result.put('nsx_endpoint', albEndPoint.nsx_endpoint);
    result.put('cloud', albEndPoint.cloud);
    result.put('tier1', albEndPoint.tier1);
    result.put('segment', albEndPoint.segment);
    result.put('subnet', albEndPoint.subnet);
}
return result;
