dunes-script-module.script:
function CustomHostNameLibConfigurator() { }
CustomHostNameLibConfigurator.prototype.configure = function (input) {
    var configPath = "PSCoE/PpS/Custom Hostname Configuration";
    input.readNumber("customhostname_lib.startindex").default(1).config(configPath).set("startindex");
    input.readBoolean("customhostname_lib.shouldAutofillIndexes").default(false).config(configPath).set("shouldAutofillIndexes");
    input.readNumber("customhostname_lib.maxNameGenerationAttempts").default(3).config(configPath).set("maxNameGenerationAttempts");
};
return CustomHostNameLibConfigurator;
