dunes-script-module.script:
function StoragePoliciesConfigurator() { }
StoragePoliciesConfigurator.prototype.configure = function (input) {
    var configPath = "NYC/StoragePolicies";
    input.readComposite("storagepolicies.envRoleMapping")
        .polymorphic()
        .list()
        .config(configPath)
        .set("envRoleMapping");
};
return StoragePoliciesConfigurator;
