dunes-script-module.script:
var message = '';
var osMessage = os.toLowerCase() === 'w' ? 'Letter' : 'Mount path';
var uniqueMountPoint = [];
if (!disk_info || !os) {
    return message;
}
disk_info.forEach(function (disk) {
    if (uniqueMountPoint.indexOf(disk.letter) >= 0) {
        message = osMessage + " is duplicated for '" + disk.letter + "', fix it and try again.";
    }
    uniqueMountPoint.push(disk.letter);
});
return message;
