dunes-script-module.script:
var result = '';
switch (logicOperation) {
    case '=':
        if (operand1 == operand2)
            result = thenvalue;
        else
            result = elsevalue;
        break;
    case '!=':
        if (operand1 != operand2)
            result = thenvalue;
        else
            result = elsevalue;
        break;
}
return result;
